void US_Hydrodyn_Cluster_Dammin::save() {
  QString use_dir = ((US_Hydrodyn *)us_hydrodyn)->somo_dir + QDir::separator() +
                    "cluster" + QDir::separator() + "parameters";
  ((US_Hydrodyn *)us_hydrodyn)
      ->select_from_directory_history(use_dir, this, true);
  QString filename = QFileDialog::getSaveFileName(
      this, us_tr("Save the parameters"), use_dir, "*.cluster_dammin");

  if (!filename.isEmpty()) {
    ((US_Hydrodyn *)us_hydrodyn)->add_to_directory_history(filename);
    if (!filename.contains(QRegExp("\\.cluster_dammin$"))) {
      filename += ".cluster_dammin";
    }
    QFile f(filename);
    if (!f.open(QIODevice::WriteOnly)) {
      QMessageBox::information(
          this,
          us_tr(QString("%1: Save").arg("US-SOMO: DAMMIN cluster interface")),
          QString(us_tr("Could not open file %1 for writing")).arg(filename));
      return;
    }
    QTextStream ts(&f);
    ts << US_Json::compose(*parameters);
    f.close();
  }
}

void US_Hydrodyn_Cluster_Dammin::load() {
  QString use_dir = ((US_Hydrodyn *)us_hydrodyn)->somo_dir + QDir::separator() +
                    "cluster" + QDir::separator() + "parameters";
  ((US_Hydrodyn *)us_hydrodyn)
      ->select_from_directory_history(use_dir, this, true);
  QString filename = QFileDialog::getOpenFileName(
      this, us_tr("Load parameters"), use_dir, "*.cluster_dammin");

  if (!filename.isEmpty()) {
    ((US_Hydrodyn *)us_hydrodyn)->add_to_directory_history(filename);
    QFile f(filename);
    if (!f.open(QIODevice::ReadOnly)) {
      QMessageBox::information(
          this,
          us_tr(QString("%1: Open").arg("US-SOMO: DAMMIN cluster interface")),
          QString(us_tr("Could not open file %1 for reading")).arg(filename));
      return;
    }
    QString qs;
    QTextStream ts(&f);
    while (!ts.atEnd()) {
      qs += ts.readLine();
    }
    f.close();
    *parameters = US_Json::split(qs);
    update_fields();
  }
}

